src="https://images.unsplash.com/photo-1564591167348-6a45f42dc223?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1074&q=80"

import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./sidebar.css";

export default function Sidebar() {
  const [cats, setCats] = useState([]);

  useEffect(() => {
    const getCats = async () => {
      const res = await axios.get("/categories");
      setCats(res.data);
    };
    getCats();
  }, []);
  return (
    <div className="sidebar">
      <div className="sidebarItem">
        <span className="sidebarTitle">ABOUT ME</span>
        <img
          src="https://i.pinimg.com/236x/1e/3f/58/1e3f587572a7a7b20bbf1828595a1786--holiday-party-themes-holiday-gift-guide.jpg"
          alt=""
        />
        <p>
        “Don’t focus on having a great blog. Focus on producing a blog that’s great for your readers.”
        </p>
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">CATEGORIES</span>
        <ul className="sidebarList">
          {cats.map((c) => (
            <Link to={`/?cat=${c.name}`} className="link">
            <li className="sidebarListItem">{c.name}</li>
            </Link>
          ))}
        </ul>
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">FOLLOW US</span>
        <div className="sidebarSocial">
        <a href="https://www.facebook.com"><i className="sidebarIcon fa-brands fa-facebook"></i></a>
        <a href="https://www.instagram.com/accounts/login/?hl=en"><i className="sidebarIcon fa-brands fa-instagram"></i></a>
        <a href="https://www.pinterest.com"><i className="sidebarIcon fa-brands fa-pinterest"></i></a>
        <a href="https://www.twitter.com"><i className="sidebarIcon fa-brands fa-twitter"></i></a>
        </div>
      </div>
    </div>
  );
}
