import React from './register.css'
import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";

export default function Register() {

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
      const res = await axios.post("/auth/register", {
        username,
        email,
        password,
      });
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className='register'>
    <span className="rtitle">Register</span>
        <form className='rform'>
            <label>Username</label>
            <input type="text" className='rinput' placeholder="Enter your Username.."/>
            <label>Email</label>
            <input type="text" className='rinput' placeholder="Enter your Email.."/>
            <label>Password</label>
            <input type="password" className='rinput' placeholder='Enter your Password..'/>
            <button className='rbutton'>Register</button>
        </form>
        <button className='rloginbutton'>Login</button>
    </div>
  )
}
