import React from './register.css'
import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";

export default function Register() {

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
      const res = await axios.post("/auth/register", {
        username,
        email,
        password,
      });
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className='register'>
    <span className="rtitle">Register</span>
        <form className='rform' onSubmit={handleSubmit}>
            <label>Username</label>
            <input type="text" className='rinput' placeholder="Enter your Username.." onChange={(e) => setUsername(e.target.value)}/>
            <label>Email</label>
            <input type="text" className='rinput' placeholder="Enter your Email.." onChange={(e) => setEmail(e.target.value)}/>
            <label>Password</label>
            <input type="password" className='rinput' placeholder='Enter your Password..' onChange={(e) => setPassword(e.target.value)}/>
            <button className='rbutton' type="submit">Register</button>
        </form>
        <button className='rloginbutton'>
          <Link className="link" to="/login">
            Login
          </Link>
        </button>
        {error && <span style={{color:"red", marginTop:"10px"}}>Something went wrong!</span>}
    </div>
  )
}
