// import './login.css'
import axios from "axios";
import { useContext, useRef } from "react";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";
import "./login.css";

export default function Login() {

  const userRef = useRef();
  const passwordRef = useRef();
  const { dispatch, isFetching } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch({ type: "LOGIN_START" });
    try {
      const res = await axios.post("/auth/login", {
        username: userRef.current.value,
        password: passwordRef.current.value,
      });
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
    } catch (err) {
      dispatch({ type: "LOGIN_FAILURE" });
    }
  };

  return (
    <div className='login'>
    <span className="ltitle">Login</span>
        <form className='lform' onSubmit={handleSubmit}>
            <label></label>
            <input type="text" className='linput' placeholder="Enter your Email.." ref={userRef}/>
            <label>Password</label>
            <input type="password" className='linput' placeholder='Enter your Password..' ref={passwordRef}/>
            <button className='lbutton' disabled={isFetching} type="submit">Login</button>
        </form>
        <button className='lregisterbutton'>
        <Link className="link" to="/register">Register</Link></button>
    </div>
  )
}
