import "./settings.css";
import Sidebar from "../../components/sidebar/sidebar";
import { useContext, useState } from "react";
import { Context } from "../../context/Context";
import axios from "axios";

export default function Settings() {
  const [file, setFile] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [success, setSuccess] = useState(false);

  const { user, dispatch } = useContext(Context);
  const PF = "http://localhost:5000/images/"

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch({ type: "UPDATE_START" });
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password,
    };
    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      updatedUser.profilePic = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {}
    }
    try {
      const res = await axios.put("/users/" + user._id, updatedUser);
      setSuccess(true);
      dispatch({ type: "UPDATE_SUCCESS", payload: res.data });
    } catch (err) {
      dispatch({ type: "UPDATE_FAILURE" });
    }
  };

  return (
    <div className='settings'>
        <div className='swrapper'>
            <div className='stitle'>
                <span className='supdatetitle'>Update Your Profile</span>
                <span className='sdeletetitle'>Delete Profile</span>
            </div>
            <form className='sform' onSubmit={handleSubmit}>
                <label>Profile Picture</label>
                <div className='spp'>
                    <img
                        alt='pp'
                        src={file ? URL.createObjectURL(file) : PF+user.profilePic}
                    />
                    <label htmlFor='fileinput'>
                    <i className="icon fa-solid fa-user"></i>
                    </label>
                    <input type="file" id='fileinput' style={{display:'none'}} onChange={(e) => setFile(e.target.files[0])}/>
                </div>
                <label>Username</label>
                <input type="text" placeholder='Abhishek' onChange={(e) => setUsername(e.target.value)}/>
                <label>Email</label>
                <input type="Email" placeholder='abhi@gmail.com' onChange={(e) => setEmail(e.target.value)}/>
                <label>Password</label>
                <input type="password"/>
                <button className='supdate'>Update</button>
            </form>
        </div>
        <Sidebar/>
    </div>
  )
}
